import React from 'react'
import Comment from './Comment'

const comments = [
    {
        id:1,
        name:"최지원",
        comment:"안녕하세요 최지원입니다.",
        path:"data:image/jpeg;base64,"
    },
    {
        id:2,
        name:"김지원",
        comment:"안녕하세요 김김지원입니다.",
        path:"data:image/jpeg;base64,"
    },
    {
        id:3,
        name:"박지원",
        comment:"안녕하세요 박박지지원원입니다.",
        path:"https://ileschool.com/data/SiteLink/a-814.jpg"
    }
]
/*
    js에서
    (배열, 리스트).map(반복시 실행할 함수)

    map() => 배열의 요소를 전부 사용하여 동일한 길이의 새로운 배열을 리턴
*/

const commentList = () => {
  return (
    <div>
       {/* <Comment name={comment[0].name} comment={comment[0].comment} path={comment[0].path} />
       <Comment name={comment[1].name} comment={comment[2].comment} path={comment[1].path} />
       <Comment name={comment[2].name} comment={comment[2].comment} path={comment[2].path} /> */}

        {
            comments.map(c=>{
                return <Comment key={c.id} name={c.name} comment={c.comment} path={c.path} />
            })


        }
    </div>
  )
}

export default commentList;